import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-category-section',
  templateUrl: './category-section.component.html',
  styleUrls: ['./category-section.component.scss'],
})
export class CategorySectionComponent implements OnInit {
  products: any[] = [];

  constructor(private http: HttpClient) {}

  ngOnInit() {
    this.loadProducts();
  }

  loadProducts() {
    this.http.get<any[]>('https://amontefusco-002-site1.ktempurl.com/api/Products/all')
      .subscribe(data => {
        this.products = data;
      }, error => {
        console.error('Error loading products', error);
      });
  }
}
<ion-grid>
  <ion-row class="ion-justify-content-center">
    <ion-col size="12" class="ion-text-center">
      <img src="assets/bebidas.png" alt="Bebidas" class="category-image"/>
    </ion-col>
  </ion-row>
  <ion-row>
    <ion-col size="6">
      <h2>Categorías</h2>
    </ion-col>
    <ion-col size="6" class="ion-text-end">
      <ion-button>Ver Más</ion-button>
    </ion-col>
  </ion-row>
  <ion-row>
    <ion-col *ngFor="let product of products" size="6">
      <ion-card>
        <img [src]="product.imageUrl" alt="{{ product.name }}" />
        <ion-card-content>
          <h3>{{ product.name }}</h3>
        </ion-card-content>
      </ion-card>
    </ion-col>
  </ion-row>
</ion-grid>
